import React, { Component } from "react"
import './imageModel.sass'
import { Modal, Button } from 'react-bootstrap'
import API from "../../utils/API"


export default class ImageModel extends Component {

  state = {
    show: false,
    image: this.props.viewImage,
    history: this.props.history,
    user: "",
    userId: "",
    sol: "?"
  }



  saveImage = () => {

    const userImage = {
      image: {
        name: "img_"+this.state.image.id,
        sol: this.state.sol,
        image: this.state.image
      },
      id: this.state.userId
    }

    if (this.state.userId && this.state.user) {
      API.saveUserImage(userImage)
        .then(payload=>{
          console.log('response payload to saveUser Image, ', payload)
          if (payload.data === "alreadySaved") {
            alert("üòúYou've already saved this Image")
            this.setState({ show: false })
          } else {
            alert("image saved")
            this.setState({ show: false })
          }
        })
        .catch(err => console.log(err))
    } else {
      alert("üò∂To save this image you must be logged In.\n\nWe'll redirect you back here after you've successfully logged in or signed up. üëç")
      this.state.history.push('/signin')
    }

  }

  render() {
    return (
      <div>
        <Modal size="xl" className="bg-light"
          show={this.props.viewImage.show}
          onHide={this.props.handleClose}
        >
          <Modal.Header closeButton>
            <Modal.Title>

            {/* Date taken: {this.props.viewImage.earth_date} */}

            </Modal.Title>
          </Modal.Header>
          <Modal.Body>

            <div>Image ID: {this.props.viewImage.image}</div>

            {/* <img src={this.props.viewImage.img_src} className="modal-image" alt="mars"/> */}

          </Modal.Body>
          <Modal.Footer>
            <Button variant="primary" onClick={this.saveImage}>
              Save Image
            </Button>
          </Modal.Footer>
        </Modal>
      </div>
    );
  }
}
