import React, { Component } from "react"
import API from "../../utils/API"
import { InputGroup, FormControl, Container, Button, Row, Col } from 'react-bootstrap'
import "./signIn.sass"

export default class User extends Component {

  state = {
    pageData: this.props.pageData,
    history: this.props.history,
    site_state: this.props.site_state,
    username: "",
    email: "",
    password: "",
    confirm: "",
    focus: "username",
    isSignUp: "false"
  }

  componentDidMount(){
    this.getUsers()
  }

  getUsers(){
    API.getUsers()
      .then(res => this.setState({users: res.data}))
      .catch(err => console.log(err))
  }

  toggleSignInUp = () => {
    this.setState({
      isSignUp: this.state.isSignUp === "false" ? "true" : "false",
      username: "",
      email: "",
      password: "",
      confirm: "",
      focus: "username",
    })

  }

  handleInputChange = (event) => {
    this.setState({[event.target.name]: event.target.value})
  }

  createUser = (event) => {
    const { userName, password, email } = this.state
    if (this.state.isSignUp === "true") {
      if (this.state.confirm !== this.state.password) {
        alert("your password and your confirm password did not match")
        this.setState({password: "", confirm: ""})
      } else {
        API.createUser({
            userName: this.state.username,
            password: this.state.password,
            email: this.state.email
          })
          .then(res => {
            console.log('new user response: ', res.data)
            this.setState({users: res.data})
          })
          .catch(err => console.log(err))
      }
    } else {
      API.signIn({ userName: this.state.username, password: this.state.password})
        .then(res => {
          console.log('Signin response: ', res.data)
          this.setState({users: res.data})
        })
        .catch(err => console.log(err))
    }

  }

  render(){
    return (
      <div className="signin">

        <Button onClick={this.state.history.goBack}
          className="back-btn"
          variant="outline text-secondary">
          Back
        </Button>

        <br />
        <h1 className="text-secondary">
          {this.state.pageData.title}
        </h1>
        <br />

        <Container>
          <Row className="justify-content-md-center">
            <Col lg="6">

              <InputGroup size="lg" className="input">
                <InputGroup.Prepend >
                  <InputGroup.Text id="inputGroup-sizing-lg"
                  className="bg-secondary text-primary input-label border-light" >
                    User Name:
                  </InputGroup.Text>
                </InputGroup.Prepend>
                <FormControl
                  className="bg-light border-light"
                  name="username"
                  onChange={this.handleInputChange}
                  value={this.state.username}
                  autoFocus={this.state.focus === "username"}
                />
              </InputGroup>

              {this.state.isSignUp === "true" &&
                <InputGroup size="lg" className="input">
                  <InputGroup.Prepend>
                    <InputGroup.Text id="inputGroup-sizing-lg"
                      className="bg-secondary text-primary input-label border-light" >
                      Email:
                    </InputGroup.Text>
                  </InputGroup.Prepend>
                  <FormControl
                    className="bg-light border-light"
                    name="email"
                    value={this.state.email}
                    onChange={this.handleInputChange}
                  />
                </InputGroup>
              }

              <InputGroup size="lg" className="input">
                <InputGroup.Prepend>
                  <InputGroup.Text id="inputGroup-sizing-lg"
                    className="bg-secondary text-primary input-label border-light" >
                    Password:
                  </InputGroup.Text>
                </InputGroup.Prepend>
                <FormControl
                  className="bg-light border-light"
                  name="password"
                  value={this.state.password}
                  onChange={this.handleInputChange}
                />
              </InputGroup>

              {this.state.isSignUp === "true" &&
                <InputGroup size="lg" className="input">
                  <FormControl
                    placeholder="...Confirm Password"
                    className="bg-light border-light"
                    name="confirm"
                    value={this.state.confirm}
                    onChange={this.handleInputChange}
                  />
                </InputGroup>
              }

              <hr/>

              <Button size="lg" variant="primary text-dark" className="w-100"
                onClick={this.createUser}>
                {this.state.isSignUp === "false" ? "Sign In" : "Sign Up" }
              </Button>


            </Col>
          </Row>
        </Container>

        <br />
        <hr />
        <br />

        <Button size="lg" variant="outline-primary text-dark" className="w-25"
          onClick={this.toggleSignInUp}>
          {this.state.isSignUp === "false" ? "New ? Sign Up" : "Already have an Account ? Sign In"}
        </Button>

        <br /><br />

      </div>
    )
  }

}
