import React, { Component } from "react"
import API from "../../utils/API"
import { InputGroup, FormControl, Container, Button, Row, Col } from 'react-bootstrap'
import "./signIn.sass"

export default class User extends Component {

  state = {
    pageData: this.props.pageData,
    history: this.props.history,
    users: []
  }

  componentDidMount(){
    console.log('mounted')
    this.getUsers()
  }

  getUsers(){
    API.getUsers()
      .then(res => {
        console.log('all users', res.data)
        this.setState({users: res.data})
      })
      .catch(err => console.log(err))
  }

  render(){
    return (
      <div className="signin">

        <br />
        <h1 className="text-secondary">
          {this.state.pageData.title}
        </h1>
        <br />

        <Container>
          <Row className="justify-content-md-center">
            <Col lg="6">

              <InputGroup size="lg" className="input">
                <InputGroup.Prepend>
                  <InputGroup.Text id="inputGroup-sizing-lg"
                    className="bg-warning text-success input-label" >
                    User Name:
                  </InputGroup.Text>
                </InputGroup.Prepend>
                <FormControl
                  className="bg-light"
                  aria-label="Large"
                  aria-describedby="inputGroup-sizing-sm"
                  // name="sol"
                  // onChange={this.handleInputChange}
                  // value={this.state.sol}
                />
              </InputGroup>

              <InputGroup size="lg" className="input">
                <InputGroup.Prepend>
                  <InputGroup.Text id="inputGroup-sizing-lg"
                    className="bg-secondary text-primary input-label" >
                    Email:
                  </InputGroup.Text>
                </InputGroup.Prepend>
                <FormControl
                  className="bg-light"
                  aria-label="Large"
                  aria-describedby="inputGroup-sizing-sm"
                  // name="page"
                  // onChange={this.handleInputChange}
                  // value={this.state.page}
                />
              </InputGroup>

              <InputGroup size="lg" className="input">
                <InputGroup.Prepend>
                  <InputGroup.Text id="inputGroup-sizing-lg"
                    className="bg-secondary text-primary input-label" >
                    Password:
                  </InputGroup.Text>
                </InputGroup.Prepend>
                <FormControl
                  className="bg-light"
                  aria-label="Large"
                  aria-describedby="inputGroup-sizing-sm"
                  // name="page"
                  // onChange={this.handleInputChange}
                  // value={this.state.page}
                />
              </InputGroup>

              <InputGroup size="lg" className="input">

                <FormControl
                  className="bg-light"
                  aria-label="Large"
                  aria-describedby="inputGroup-sizing-sm"
                  placeholder="...Confirm Password"
                  // name="page"
                  // onChange={this.handleInputChange}
                  // value={this.state.page}
                />
              </InputGroup>

              <br />
              <Button size="lg" variant="success" className="w-100"
                onClick={this.findPage}>
                Sign Up
              </Button>
            </Col>
          </Row>
        </Container>

        <br />
        <hr />
        <br />

        <h1 className="text-info">
          Sign In
        </h1>

        { this.state.users.length > 0 &&
          this.state.users.map(user=>(
            <div key={user._id}>
              <Button variant="outline-secondary" size="lg" className="btn">
                {user.userName}
              </Button>
              <br /><br />
            </div>
          ))
        }

        <br /><br />
        <Button onClick={this.state.history.goBack}> Go Back </Button>

      </div>
    )
  }

}
