import React, { Component } from "react"
import Footer from "../../components/Footer/footer.js"
import { Card } from "react-bootstrap"
import API from "../../utils/API"
import "./observations.sass"

export default class Obervations extends Component {

  constructor(props){
    super()
    this.state = {
      pageData: props.pageData,
      images: []
    }
  }

  componentDidMount(){
    this.getAllSavedImages()
  }

  getAllSavedImages = () => {
    // this.setState({viewImage: {}})
    API.getAllSavedImages()
      .then(res => {
        console.log(res.data)
        this.setState({images: res.data || []})
      })
      .catch(err => console.log(err))
  }

  render() {
    return (
      <div className="theme">

        <br /><br />
        <h1 className="text-info">
          {this.state.pageData.title}
        </h1>
        <br /><br />

        {this.state.images.length > 0 &&
          this.state.images.map( (img, i) => (
            <Card className="card bg-secondary" key={img.image.id}>
              <Card.Img variant="top" src={img.image.img_src} />
              <Card.Body>
               <Card.Title>Title</Card.Title>
               <Card.Text>
                 ...text {img.id}
               </Card.Text>
              </Card.Body>
            </Card>
          ))
        }

        <Footer />

      </div>
    )
  }

}
