import React, { Component } from "react"
import Footer from "../../components/Footer/footer.js"
import Rover from "../../images/rover.svg"

import "./home.sass"

export default class Home extends Component {

  constructor(props){
    super(props)
    this.state = {
      rootData: props.rootData,
      manifest: props.manifest,
      admin: props.admin
    }
  }

  componentDidMount(){
    console.log('mount', this.state.manifest)
    this.setState({manifest: this.state.manifest})
  }

  componentDidUpdate(){
    console.log('update', this.state.manifest)
  }

  componentWillReceiveProps({manifest, admin}){
    console.log('recieve')
    const obj = {...manifest}
    let totalPhotos = manifest.total_photos.toString()
    console.log(totalPhotos.replace(/\B(?=(\d{3})+(?!\d))/g, ","))
    obj.total_photos = totalPhotos.replace(/\B(?=(\d{3})+(?!\d))/g, ",")
    this.setState({
      manifest: obj,
      admin: admin
    })

  }

  dressNum(n){
    console.log(n)
    // let nn = n.toString()
    // nn = nn.replace(/\B(?=(\d{3})+(?!\d))/g, ","))
    return nn
  }

  handleImageLoaded() {
    this.setState({ imageStatus: "loaded" }) // this will allow things to load in their proper place / time. like the footer, who needs this images size to determine position.
  }

  render() {
    return (
      <div className="home">

        <div className="imageContainer">
          <img src={Rover} alt="Rover" className="roverSVG"
            onLoad={this.handleImageLoaded.bind(this)}
          />
        </div>

        <h1 className="text-warning">
          &bull; Mission Manifest &bull;
        </h1>

        <div>
          <h1 className="text-secondary">?</h1> <br/ >
        </div>

        <h4 className="text-primary">
          this.state.ph {this.state.ph}
        </h4>
        <h4 className="text-primary">
          this.ph: {this.ph}
        </h4>
        <h4 className="text-primary">
          from app's manifest:
          {this.state.manifest &&
            <div>
              {this.dressNum(this.state.manifest.total_photos)}
            </div>
          }
        </h4>



        <Footer />

      </div>
    )
  }


}

