import React, { Component } from "react"
import API from "../../utils/API"
import { InputGroup, FormControl, Container, Button, Row, Col } from 'react-bootstrap'
import "./signIn.sass"

export default class User extends Component {

  state = {
    pageData: this.props.pageData,
    history: this.props.history,
    site_state: this.props.site_state,
    users: []
  }

  componentDidMount(){
    console.log(this.state)
    this.getUsers()
  }

  getUsers(){
    API.getUsers()
      .then(res => {
        console.log('all users', res.data)
        this.setState({users: res.data})
      })
      .catch(err => console.log(err))
  }

  render(){
    return (
      <div className="signin">

        <Button onClick={this.state.history.goBack}
          className="back-btn"
          variant="outline-primary text-dark">
          Go Back
        </Button>

        <br />
        <h1 className="text-secondary">
          {this.state.pageData.title}
        </h1>
        <br />

        <Container>
          <Row className="justify-content-md-center">
            <Col lg="6">

              <InputGroup size="lg" className="input">
                <InputGroup.Prepend >
                  <InputGroup.Text id="inputGroup-sizing-lg"
                  className="bg-secondary text-primary input-label border-light" >
                    User Name:
                  </InputGroup.Text>
                </InputGroup.Prepend>
                <FormControl
                  className="bg-light border-light"
                  aria-label="Large"
                  aria-describedby="inputGroup-sizing-sm"
                  // name="sol"
                  // onChange={this.handleInputChange}
                  // value={this.state.sol}
                />
              </InputGroup>

              <InputGroup size="lg" className="input">
                <InputGroup.Prepend>
                  <InputGroup.Text id="inputGroup-sizing-lg"
                    className="bg-secondary text-primary input-label border-light" >
                    Email:
                  </InputGroup.Text>
                </InputGroup.Prepend>
                <FormControl
                  className="bg-light border-light"
                  aria-label="Large"
                  aria-describedby="inputGroup-sizing-sm"
                  // name="page"
                  // onChange={this.handleInputChange}
                  // value={this.state.page}
                />
              </InputGroup>

              <InputGroup size="lg" className="input">
                <InputGroup.Prepend>
                  <InputGroup.Text id="inputGroup-sizing-lg"
                    className="bg-secondary text-primary input-label border-light" >
                    Password:
                  </InputGroup.Text>
                </InputGroup.Prepend>
                <FormControl
                  className="bg-light border-light"
                  aria-label="Large"
                  aria-describedby="inputGroup-sizing-sm"
                  // name="page"
                  // onChange={this.handleInputChange}
                  // value={this.state.page}
                />
              </InputGroup>

              <InputGroup size="lg" className="input">

                <FormControl
                  className="bg-light border-light"
                  aria-label="Large"
                  aria-describedby="inputGroup-sizing-sm"
                  placeholder="...Confirm Password"
                  // name="page"
                  // onChange={this.handleInputChange}
                  // value={this.state.page}
                />
              </InputGroup>

              <br />
              <hr />
              <br />

              <Button size="lg" variant="outline-primary text-dark" className="w-100"
                onClick={this.findPage}>
                Sign In
              </Button>


            </Col>
          </Row>
        </Container>

        <br />
        <hr />
        <br />

        <br />
        <h1 className="text-secondary">
          Sign Up
        </h1>
        <br />

        <Button size="lg" variant="outline-primary text-dark" className="w-100"
          onClick={this.findPage}>
          New ? Sign Up
        </Button>

        <br /><br />

      </div>
    )
  }

}

