// import React, { Component } from "react"
import React from "react"
// import API from "../../utils/API"
import { InputGroup, FormControl, Container, Button, Row, Col, Card } from 'react-bootstrap'
import "./imagesContainer.sass"
import ViewImage from "../ViewImage/viewImage.js"

export default function ImagesContainer(props) {


  console.log(props)

  // state = {
  //
  // }
  //
  // viewImage = (image) => {
  //   const imageData = {
  //     image: image,
  //     user: this.state.site_state.user,
  //     userId: this.state.site_state.user_id,
  //     show: true,
  //     sol: this.state.sol
  //   }
  //   this.setState({viewImage: imageData})
  // }

  return (
    <div className="image-container">
      <div className="card-container">
        {props.images.length > 0 &&
          props.images.map( (img, i) => (
            <Card className="card bg-secondary" key={img.id}>

              <Card.Img variant="top"
                src={img.img_src}
              />

              <Card.Body>

                <div className="text-white">
                  {img.camera.full_name}
                </div>

                <div className="text-white-bbb">
                  ID#{img.id} | earth date: {img.earth_date}
               </div>

               <Button variant="primary" className="w-100"
                onClick={()=>this.viewImage(img)}>
                  View Image
                </Button>

              </Card.Body>
            </Card>
          ))
        }
      </div>

      {/* <ViewImage
        viewImage={this.state.viewImage}
        history={this.state.history}
      /> */}

      <br /><br />
    </div>
  )

}
