import React, { Component } from "react"
import Footer from "../../components/Footer/footer.js"
import Rover from "../../images/rover.svg"

import "./home.sass"

export default class Home extends Component {

  constructor(props){
    super(props)
    this.state = {
      rootData: props.rootData,
    }
  }

  componentDidUpdate(props){
    console.log('updated', props)
  }

  componentWillReceiveProps(props){
    console.log("recieve props")
    if (props.rootData.admin) {
      const obj = {...props.rootData}
      let totalPhotos = obj.admin.mission_manifes.total_photos.toString()
      let max_sol = obj.admin.mission_manifes.max_sol.max_sol.toString()
      obj.admin.mission_manifes.total_photos = totalPhotos.replace(/\B(?=(\d{3})+(?!\d))/g, ",")
      obj.admin.mission_manifes.max_sol = max_sol.replace(/\B(?=(\d{3})+(?!\d))/g, ",")
      this.setState({rootData:obj})

    }
  }

  handleImageLoaded() {
    this.setState({ imageStatus: "loaded" }) // this will allow things to load in their proper place / time. like the footer, who needs this images size to determine position.
  }

  render() {
    return (
      <div className="home">

        <div className="imageContainer">
          <img src={Rover} alt="Rover" className="roverSVG"
            onLoad={this.handleImageLoaded.bind(this)}
          />
        </div>

        <h1 className="text-warning">
          &bull; Mission Manifest &bull;
        </h1>

        { !this.state.rootData.admin &&
          <div>
            <h1 className="text-secondary">?</h1> <br/ >
          </div>
        }

        { this.state.rootData.admin &&
          <div>
            <h4 className="text-primary">
              Launch Date: {this.state.rootData.admin.mission_manifest.launch_date}
            </h4>
            <h4 className="text-primary">
              Landing Date: {this.state.rootData.admin.mission_manifest.landing_date}
            </h4>
            <h4 className="text-primary">
              Last Updated: {this.state.rootData.admin.mission_manifest.max_date}
            </h4>
            <h4 className="text-primary">
              Martian Days("sols"): {this.state.rootData.admin.mission_manifest.max_sol}
            </h4>
            <h4 className="text-primary">
              Status: {this.state.rootData.admin.mission_manifest.status}
            </h4>
            <h4 className="text-primary">
              Total Photos: {this.state.rootData.admin.mission_manifest.total_photos}
            </h4>
            <br />
          </div>
        }



        <Footer />

      </div>
    )
  }


}

