import React, { Component } from "react"
import Footer from "../../components/Footer/footer.js"
import API from "../../utils/API"
import { InputGroup, FormControl, Container, Button, Row, Col } from 'react-bootstrap'
import "./images.sass"



export default class Images extends Component {

  constructor(props){
    super(props)
    this.state = {
      pageData: props.pageData,
      sol: "1000",
      page: "1"
    }
  }
  // state = {
  //   pageData: this.props.pageData,
  //   sol: "1000",
  //   page: "1"
  // }

  componentDidMount() {
    this.findPage()
  }

  findPage() {
    console.log(this.state.page)
    API.findPage(1000,1)
      .then(res => {
        console.log('images page payload', res.data)
      })
      .catch(err => console.log(err))
  }

  handleSolChange(event){
    console.log(event.target.name)
    console.log(event.target.value)
    console.log(this.state.page)
    this.setState({page: event.target.value})
  }

  render() {
    return (
      <div className="images">

        <br /><br />
        <h1 className="text-info">
          {this.state.pageData.title}
        </h1>
        <br /><br />

        <Container>
          <Row className="justify-content-md-center">
            <Col lg="6">
            <InputGroup size="lg" className="input">
              <InputGroup.Prepend>
                <InputGroup.Text id="inputGroup-sizing-lg"
                  className="bg-secondary text-primary input-label" >
                  Sol:
                </InputGroup.Text>
              </InputGroup.Prepend>
              <FormControl
                className="bg-light"
                aria-label="Large"
                aria-describedby="inputGroup-sizing-sm"
                name="sol"
                onChange={()=>this.handleSolChange}
                value={this.state.sol}
              />
            </InputGroup>

            <InputGroup size="lg" className="input">
              <InputGroup.Prepend>
                <InputGroup.Text id="inputGroup-sizing-lg"
                  className="bg-secondary text-primary input-label" >
                  Page:
                </InputGroup.Text>
              </InputGroup.Prepend>
              <FormControl
                className="bg-light"
                aria-label="Large"
                aria-describedby="inputGroup-sizing-sm"
                name="page"
                onChange={this.handleSolChange}
                value={this.state.page}
              />
            </InputGroup>

            <Button size="lg" variant="success" className="w-100"
              onClick={this.findPage}>
              Find Images
            </Button>
          </Col>
        </Row>
      </Container>



        <br /><br />


        <Footer />
      </div>
    )
  }

}
