import React, { Component } from 'react'
import Footer from "../../components/Footer/footer.js"
import Rover from '../../images/rover.svg'

import "./home.sass"

export default class Home extends Component {

  constructor(props){
    super(props)
    this.state = {
      rootData: props.rootData,
      m: {},
      c: {}
    }
  }

  componentWillReceiveProps(props){
    console.log(props)
    if (props.rootData.admin) {
      const m = props.rootData.admin.mission_manifest
      const c = {
        visits: props.rootData.admin.visits,
        images_saved: props.rootData.admin.images_saved,
        images_viewed: props.rootData.admin.images_viewed
      }
      const totalPhotos = m.total_photos.toString()
      const max_sol = m.max_sol.toString()
      m.total_photos = totalPhotos.replace(/\B(?=(\d{3})+(?!\d))/g, ",")
      m.max_sol = max_sol.replace(/\B(?=(\d{3})+(?!\d))/g, ",")
      this.setState({m: m, c: c})
    }

  }

  handleImageLoaded() {
    this.setState({ imageStatus: "loaded" }) // this will allow things to load in their proper place / time. like the footer, who needs this images size to determine position.
  }

  render() {
    return (
      <div className="home">

        <div className="imageContainer">
          <img src={Rover} alt="Rover" className="roverSVG"
            onLoad={this.handleImageLoaded.bind(this)}
          />
        </div>

        <h1 className="text-warning">
          &bull; Mission Manifest &bull;
        </h1>

        { Object.entries(this.state.m).length === 0 &&
          <div>
            <h1 className="text-secondary">?</h1> <br/ >
          </div>
        }

        { Object.entries(this.state.m).length !== 0 &&
          <div>
            <h4 className="text-primary">
              Launch Date: {this.state.m.launch_date}
            </h4>
            <h4 className="text-primary">
              Landing Date: {this.state.m.landing_date}
            </h4>
            <h4 className="text-primary">
              Last Updated: {this.state.m.max_date}
            </h4>
            <h4 className="text-primary">
              Martian Days("sols"): {this.state.m.max_sol}
            </h4>
            <h4 className="text-primary">
              Status: {this.state.m.status}
            </h4>
            <h4 className="text-primary">
              Total Photos: {this.state.m.total_photos}
            </h4>
            <br />
          </div>
        }

        <h1 className="text-warning">
          &bull; Community Activity &bull;
        </h1>

        { Object.entries(this.state.c).length === 0 &&
          <div>
            <h1 className="text-secondary">?</h1> <br/ >
          </div>
        }

        { Object.entries(this.state.c).length !== 0  &&
          <div>

            <h4 className="text-primary">
              Visits({this.state.c.visits})
            </h4>
            <h4 className="text-primary">
              Saved Images({this.state.c.images_saved})
            </h4>
            <h4 className="text-primary">
              Images Viewed({this.state.c.images_viewed})
            </h4>

            <p> </p>
            <br /><br />

          </div>
        }



        <Footer />

      </div>
    )
  }


}
