import React, { Component } from 'react'
import Footer from "../../components/Footer/footer.js"
import Rover from '../../images/rover.svg'

import "./home.sass"

export default class Home extends Component {

  constructor(props){
    super(props)
    this.state = {
      rootData: props.rootData,
    }
    this.m = {}
    this.visits = {
      visits: "?",
      saved_photos: "?",
      viewed_photos: "?",
    }
  }

  componentWillReceiveProps(props){
    this.setState({rootData: props.rootData})
    // ðŸ¤” why even have any of this in statee.s
    console.log(props)
    this.m = props.rootData.admin.mission_manifest
    // this.c = {
    //   visits = props.rootData.visits
    //
    // }
    console.log(this.m)

    let totalPhotos = this.m.total_photos.toString()
    let max_sol = this.m.max_sol.toString()
    this.m.total_photos = totalPhotos.replace(/\B(?=(\d{3})+(?!\d))/g, ",")
    this.m.max_sol = max_sol.replace(/\B(?=(\d{3})+(?!\d))/g, ",")

  }

  handleImageLoaded() {
    this.setState({ imageStatus: "loaded" }) // this will allow things to load in their proper place / time. like the footer, who needs this images size to determine position.
  }

  render() {
    return (
      <div className="home">

        <div className="imageContainer">
          <img src={Rover} alt="Rover" className="roverSVG"
            onLoad={this.handleImageLoaded.bind(this)}
          />
        </div>

        { this.state.rootData.admin.mission_manifest &&
          <div>
            <h1 className="text-warning">
              &bull; Mission Manifest &bull;
            </h1>
            <h4 className="text-primary">
              Launch Date: {this.m.launch_date}
            </h4>
            <h4 className="text-primary">
              Landing Date: {this.m.landing_date}
            </h4>
            <h4 className="text-primary">
              Last Updated: {this.m.max_date}
            </h4>
            <h4 className="text-primary">
              Martian Days("sols"): {this.m.max_sol}
            </h4>
            <h4 className="text-primary">
              Status: {this.m.status}
            </h4>
            <h4 className="text-primary">
              Total Photos: {this.m.total_photos}
            </h4>
            <br />
            <h1 className="text-warning">
              &bull; Community Activity &bull;
            </h1>
            <h4 className="text-primary">
              Visits({this.visits})
            </h4>
            <h4 className="text-primary">
              Save Photos({this.visits})
            </h4>
            <h4 className="text-primary">
              ViewedPhotos({this.visits})
            </h4>
            <p> </p>
            <br /><br />

          </div>
        }



        <Footer />

      </div>
    )
  }


}
